<?phpclass Api extends MY_Controller{	public function __construct()	{		parent::__construct();	}	public function cep($cep = FALSE)	{				$return = file_get_contents("http://viacep.com.br/ws/{$cep}/json");		exit($return);	}	public function qrcode($value = FALSE, $amount = FALSE, $size = 10)	{					error_reporting(0);			header("Content-Type: image/png");			$this->load->library('ciqrcode');			$valueok = "bitcoin:".$value."?amount=".$amount."";			$params['data'] = $valueok;			$params['size'] = $size;			$this->ciqrcode->generate($params);	}}